INSERT INTO [Product] ([SellerID], [ProductName], [Description], [Price], [Unit], [Quantity], [CategoryID], [CityID]) VALUES 
    (8, N'Cải thìa 300g', N'Cải thìa 300g – Rau sạch organic được trồng tại trang trại thân thiện với môi trường, không sử dụng hóa chất hay phân bón công nghiệp. <br> Lá xanh mướt, giòn ngọt, giàu vitamin A, C, rất phù hợp để xào, nấu canh hoặc ăn lẩu. <br> Sản phẩm là bí quyết cho bữa ăn ngon và cuộc sống khỏe mạnh của gia đình bạn.', 6000, N'300g', 27, 1, 59),
    (9, N'Cải bẹ xanh 400g', N'Cải bẹ xanh 400g – Rau sạch được trồng hoàn toàn tự nhiên tại vùng đất màu mỡ, không dùng thuốc trừ sâu, đảm bảo an toàn tuyệt đối. <br> Lá to, dày, vị ngọt thanh, lý tưởng để nấu canh, luộc hoặc xào với tỏi. <br> Lựa chọn hoàn hảo mang lại sự an tâm và dinh dưỡng cho bữa cơm gia đình.', 5000, N'400g', 25, 1, 40),
    (8, N'Cải ngọt 400g', N'Cải ngọt 400g – Rau sạch được chăm sóc tỉ mỉ từ khâu gieo trồng đến thu hoạch, đạt tiêu chuẩn chất lượng cao. <br> Thân mọng nước, lá mềm, vị ngọt tự nhiên, thích hợp để nấu canh, xào hoặc dùng trong món lẩu. <br> Sản phẩm mang đến hương vị tươi ngon và nguồn dinh dưỡng dồi dào cho mọi nhà.', 5000, N'400g', 19, 1, 54),
    (9, N'Rau dền 400g', N'Rau dền 400g – Rau sạch được trồng với tình yêu và trách nhiệm, từ hạt giống chọn lọc đến quy trình tự nhiên không hóa chất. <br> Lá đỏ hoặc xanh mềm mại, giàu sắt và vitamin, hoàn hảo để nấu canh hoặc luộc chấm mắm. <br> Sản phẩm đảm bảo chất lượng từ vườn đến bàn ăn của bạn.', 5000, N'400g', 15, 1, 33),
    (9, N'Rau mồng tơi 400g', N'Rau mồng tơi 400g – Rau sạch được chăm sóc kỹ lưỡng tại trang trại đạt chuẩn, không dùng phân bón hóa học. <br> Lá dày, mọng nước, vị mát dịu, lý tưởng để nấu canh cua, canh tôm hoặc xào tỏi. <br> Mang lại hương vị tự nhiên và sự tươi ngon cho từng bữa ăn gia đình.', 5000, N'400g', 13, 1, 32),
    (9, N'Rau muống hạt 400g', N'Rau muống hạt 400g – Rau sạch từ những luống rau xanh mướt, trồng tự nhiên không hóa chất, mang đến sự an tâm tuyệt đối. <br> Thân giòn, lá xanh đậm, thích hợp để luộc, xào hoặc nấu canh. <br> Sản phẩm là lựa chọn lý tưởng cho bữa cơm đơn giản mà trọn vẹn dinh dưỡng.', 5000, N'400g', 17, 1, 25),
    (7, N'Rau muống nước 400g', N'Rau muống nước 400g – Rau sạch tươi ngon từ vườn đến bàn ăn, được trồng ở vùng đất ngập nước tự nhiên, không dùng thuốc trừ sâu. <br> Thân mềm, lá mướt, vị ngọt đặc trưng, hoàn hảo để luộc chấm mắm hoặc xào tỏi. <br> Sản phẩm mang lại sự an toàn và hương vị dân dã cho gia đình bạn.', 5000, N'400g', 29, 1, 32),
    (9, N'Rau lang 400g', N'Rau lang 400g – Rau sạch được chăm sóc tỉ mỉ, không hóa chất, giữ nguyên độ tươi xanh từ vườn đến tay bạn. <br> Lá mềm, vị ngọt nhẹ, giàu chất xơ, thích hợp để luộc, xào hoặc nấu canh. <br> Sản phẩm là nguồn dinh dưỡng tự nhiên cho bữa ăn lành mạnh của gia đình.', 6000, N'400g', 15, 1, 35),
    (8, N'Cải ngồng 400g', N'Cải ngồng 400g – Rau sạch từ trang trại đạt chuẩn, thu hoạch đúng độ tươi ngon, không dùng chất bảo quản hay phân bón hóa học. <br> Thân giòn, lá xanh, vị ngọt thanh, lý tưởng để xào tỏi hoặc nấu canh thịt. <br> Sản phẩm mang đến sự an tâm và hương vị tự nhiên cho từng bữa cơm.', 5000, N'400g', 10, 1, 17),
    (6, N'Hành lá khoảng 100g', N'Hành lá 100g – Rau sạch được trồng với sự tận tâm, không thuốc trừ sâu, đảm bảo chất lượng từ gốc đến ngọn. <br> Lá xanh mướt, thơm nồng, hoàn hảo để nêm nếm món canh, xào hoặc làm gia vị trang trí. <br> Sản phẩm nhỏ nhưng mang lại hương vị lớn cho bữa ăn của bạn.', 4740, N'100g', 26, 1, 38),
    (6, N'Cải bẹ dún', N'Cải bẹ dún 400g – Rau sạch thu hoạch mỗi ngày từ trang trại tự nhiên, không hóa chất, giữ nguyên độ tươi xanh. <br> Lá xoăn, dày, vị ngọt nhẹ, thích hợp để nấu canh, luộc hoặc dùng trong món lẩu. <br> Sản phẩm là lựa chọn tuyệt vời cho sức khỏe và chất lượng bữa ăn gia đình.', 6000, N'400g', 9, 1, 23),
    (9, N'Hành lá 300g', N'Hành lá 300g – Rau sạch tươi ngon từ vườn đến bàn ăn, được trồng tự nhiên, không phụ gia hay hóa chất. <br> Lá dài, xanh đậm, thơm đặc trưng, lý tưởng để làm gia vị cho món canh, xào hoặc phở. <br> Sản phẩm mang lại sự an toàn và hương vị tươi mới cho từng món ăn.', 9000, N'300g', 19, 1, 48),
    (10, N'Rau ngót khoảng 250g', N'Rau ngót 250g – Rau sạch tinh khiết, không thuốc trừ sâu, không phân bón hóa học, được trồng tại vùng đất sạch. <br> Lá mềm, vị ngọt thanh, giàu vitamin C, hoàn hảo để nấu canh thịt hoặc tôm. <br> Sản phẩm mang lại nguồn dinh dưỡng tự nhiên cho sức khỏe gia đình bạn.', 8760, N'250g', 29, 1, 47),
    (9, N'Bún tươi 500g', N'Bún tươi 500g – Sản phẩm sạch từ gạo tự nhiên, không chất bảo quản, được làm mới mỗi ngày. <br> Sợi bún mềm, dai, trắng mịn, thích hợp để nấu bún bò, bún riêu hoặc ăn kèm lẩu. <br> Lựa chọn hoàn hảo mang lại sự tiện lợi và an toàn cho bữa ăn gia đình.', 8000, N'500g', 5, 1, 60),
    (10, N'Xà lách lô lô xanh', N'Xà lách lô lô xanh 300g – Rau sạch từ những luống rau xanh mướt, không hóa chất, mang đến sự an tâm tuyệt đối. <br> Lá xoăn, giòn, vị tươi mát, lý tưởng để trộn salad, ăn kèm thịt nướng hoặc làm rau sống. <br> Sản phẩm mang lại hương vị tự nhiên và dinh dưỡng cho bữa ăn của bạn.', 6300, N'300g', 12, 1, 60),
    (8, N'Rau tần ô', N'Rau tần ô 500g – Rau sạch từ trang trại tự nhiên, không dùng thuốc trừ sâu, giữ nguyên độ tươi xanh. <br> Lá dày, vị ngọt mát, giàu dinh dưỡng, hoàn hảo để nấu canh gà hoặc cá. <br> Sản phẩm là lựa chọn tuyệt vời mang lại sức khỏe và hương vị dân dã cho gia đình.', 9000, N'500g', 27, 1, 63),
    (8, N'Lá mơ 100g', N'Lá mơ 100g – Rau sạch thu hoạch mỗi ngày, không hóa chất, giữ nguyên hương vị đặc trưng và độ tươi ngon. <br> Lá mềm, thơm nồng, thích hợp để ăn kèm lẩu, tráng trứng hoặc làm gia vị. <br> Sản phẩm nhỏ gọn mang lại sự tiện lợi và chất lượng cho bữa ăn của bạn.', 6000, N'100g', 18, 1, 62),
    (9, N'Hành lá, ngò rí 100g', N'Hành lá, ngò rí 100g – Rau sạch tinh khiết, không thuốc trừ sâu, không phân bón hóa học, được trồng tự nhiên. <br> Hành thơm, ngò rí tươi, lý tưởng để nêm nếm món canh, phở hoặc trang trí món ăn. <br> Sản phẩm mang lại hương vị tươi mới và dinh dưỡng cho từng bữa cơm.', 5400, N'100g', 28, 1, 61),
    (9, N'Rau răm 50g', N'Rau răm 50g – Rau sạch organic từ trang trại thân thiện với môi trường, không hóa chất, đảm bảo an toàn. <br> Lá nhỏ, thơm nồng, thích hợp để ăn kèm gỏi gà, hột vịt lộn hoặc làm gia vị. <br> Sản phẩm là bí quyết mang lại hương vị độc đáo và sức khỏe cho gia đình bạn.', 3000, N'50g', 21, 1, 60),
    (6, N'Ngò gai, rau om 50g', N'Ngò gai, rau om 50g – Rau sạch được chăm sóc tỉ mỉ, đạt tiêu chuẩn chất lượng cao, không dùng phụ gia. <br> Ngò gai thơm, rau om mát, hoàn hảo để nấu canh chua hoặc làm gia vị cho món cá. <br> Sản phẩm mang lại hương vị tự nhiên và sự tươi ngon cho bữa ăn của bạn.', 3600, N'50g', 21, 1, 62),
    (10, N'Rau cần tàu (cần ta) 100g', N'Rau cần tàu 100g – Rau sạch organic từ vùng đất tự nhiên, không hóa chất, là lựa chọn cho cuộc sống khỏe mạnh. <br> Thân giòn, lá thơm, vị ngọt nhẹ, thích hợp để xào thịt bò hoặc nấu lẩu. <br> Sản phẩm mang lại sự an tâm và dinh dưỡng tinh khiết cho gia đình bạn.', 3900, N'100g', 14, 1, 55),
    (6, N'Ngò rí 50g', N'Ngò rí 50g – Rau sạch được trồng tự nhiên, không hóa chất, đảm bảo an toàn cho sức khỏe gia đình bạn. <br> Lá nhỏ, thơm đặc trưng, lý tưởng để nêm nếm món phở, canh hoặc trang trí món ăn. <br> Sản phẩm nhỏ gọn mang lại hương vị tươi mới và chất lượng cho bữa cơm.', 3300, N'50g', 19, 1, 32),
    (8, N'Xà lách thủy tinh thủy canh khoảng 200g', N'Xà lách thủy tinh 200g – Rau sạch trồng thủy canh hiện đại, thu hoạch mỗi ngày, không hóa chất, giữ độ tươi xanh tuyệt đối. <br> Lá giòn, vị mát, hoàn hảo để trộn salad, ăn kèm đồ nướng hoặc làm rau sống. <br> Sản phẩm mang lại sự tinh khiết và chất lượng cao cho bữa ăn của bạn.', 9600, N'200g', 25, 1, 37),
    (9, N'Rau đắng khoảng 200g', N'Rau đắng 200g – Rau sạch thu hoạch mỗi ngày từ vùng đất tự nhiên, không phụ gia, giữ nguyên vị đắng đặc trưng. <br> Lá nhỏ, giàu dinh dưỡng, thích hợp để nấu canh hoặc ăn kèm lẩu cá. <br> Sản phẩm mang lại hương vị dân dã và lợi ích sức khỏe cho gia đình bạn.', 5400, N'200g', 18, 1, 16),
    (8, N'Rau diếp cá khoảng 100g', N'Rau diếp cá 100g – Rau sạch trồng tự nhiên, không hóa chất, đảm bảo an toàn và chất lượng từ vườn đến bàn ăn. <br> Lá thơm, vị mát, giàu vitamin, lý tưởng để ăn sống, làm nước ép hoặc nấu canh. <br> Sản phẩm mang lại sự tươi mát và dinh dưỡng cho cuộc sống hàng ngày.', 4200, N'100g', 8, 1, 37),
    (9, N'Rau húng cây khoảng 100g', N'Rau húng cây 100g – Rau sạch từ đất mẹ, không hóa chất, mang lại hương vị tự nhiên và chất lượng vượt trội. <br> Lá thơm, vị the mát, thích hợp để ăn kèm phở, gỏi hoặc làm gia vị. <br> Sản phẩm là món quà thiên nhiên dành cho bữa ăn ngon và khỏe mạnh.', 4200, N'100g', 14, 1, 17),
    (8, N'Rau húng quế khoảng 100g', N'Rau húng quế 100g – Rau sạch trồng tự nhiên, không phụ gia, đảm bảo an toàn cho sức khỏe gia đình bạn. <br> Lá thơm nồng, vị ngọt nhẹ, lý tưởng để ăn kèm bún bò, gỏi hoặc làm gia vị. <br> Sản phẩm mang lại sự tươi mới và hương vị đặc trưng cho từng món ăn.', 4200, N'100g', 6, 1, 47),
    (8, N'Tía tô khoảng 100g', N'Tía tô 100g – Rau sạch thu hoạch mỗi ngày, không hóa chất, giữ nguyên độ tươi xanh và chất lượng tuyệt vời. <br> Lá tím đẹp, thơm đặc trưng, thích hợp để ăn sống, nấu cháo hoặc làm gia vị. <br> Sản phẩm mang lại hương vị độc đáo và lợi ích sức khỏe cho gia đình bạn.', 4200, N'100g', 8, 1, 20),
    (10, N'Giá sống khoảng 200g', N'Giá sống 200g – Sản phẩm sạch thu hoạch mỗi ngày, không chất bảo quản, giữ nguyên độ tươi ngon và giòn rụm. <br> Giá trắng, mọng nước, lý tưởng để ăn kèm phở, gỏi cuốn hoặc xào. <br> Sản phẩm mang lại sự tiện lợi và chất lượng cho bữa ăn hàng ngày của bạn.', 4080, N'200g', 19, 1, 10),
    (8, N'Rau má khoảng 200g', N'Rau má 200g – Rau sạch từ thiên nhiên, không chỉ là thực phẩm mà còn là cam kết về sức khỏe và sự bền vững. <br> Lá nhỏ, vị mát, giàu vitamin, hoàn hảo để làm nước ép, nấu canh hoặc ăn sống. <br> Sản phẩm tươi sạch mỗi ngày, mang lại sự an tâm cho gia đình bạn.', 7200, N'200g', 26, 1, 4);
INSERT INTO [Product] ([SellerID], [ProductName], [Description], [Price], [Unit], [Quantity], [CategoryID], [CityID]) 
VALUES 
    (6, N'Bắp cải trắng', N'Bắp cải trắng được trồng hoàn toàn theo phương pháp hữu cơ tại các trang trại sạch, không sử dụng thuốc trừ sâu hay phân bón hóa học. <br> Từng lá bắp cải giòn, trắng, giàu dinh dưỡng, là lựa chọn hoàn hảo cho các món canh, salad hay xào. <br> Sản phẩm cam kết mang đến sự an tâm tuyệt đối cho sức khỏe gia đình bạn, từ những luống rau xanh mướt đến bàn ăn ấm cúng.', 11000, N'1kg', 5, 2, 30),
    (6, N'Bắp cải thảo', N'Bắp cải thảo organic – món quà từ thiên nhiên, được trồng với sự chăm chút tỉ mỉ trong môi trường không hóa chất. <br> Lá mỏng, mềm, ngọt tự nhiên, rất lý tưởng để nấu canh, làm kim chi hay các món cuốn hấp dẫn. <br> Đây không chỉ là rau sạch mà còn là cam kết về một lối sống xanh, bền vững, giúp bạn và gia đình tận hưởng cuộc sống khỏe mạnh hơn mỗi ngày.', 12000, N'1kg', 25, 2, 36),
    (8, N'Bắp cải thảo túi 1kg', N'Bắp cải thảo túi 1kg – sự kết hợp hoàn hảo giữa chất lượng và tiện lợi, được đóng gói từ những cây rau tươi ngon nhất. <br> Sản phẩm không chỉ đảm bảo không hóa chất, không thuốc trừ sâu mà còn giữ nguyên độ tươi giòn tự nhiên nhờ thu hoạch hàng ngày. <br> Với trọng lượng từ 1-2 bắp, đây là lựa chọn tuyệt vời cho các món ăn gia đình, mang đến sức khỏe và sự bền vững từ thiên nhiên.', 12000, N'/Từ 1-2 bắp', 18, 2, 45),
    (6, N'Bầu sao', N'Bầu sao được trồng tự nhiên tại các trang trại rau sạch, không sử dụng bất kỳ hóa chất nào, đảm bảo an toàn tuyệt đối cho sức khỏe. <br> Trái bầu mọng nước, thịt dày, vị ngọt thanh, thích hợp cho các món canh, luộc hay xào. <br> Mỗi quả bầu là kết tinh của sự chăm sóc tận tâm, mang đến hương vị quê nhà giản dị nhưng đậm đà, bổ sung dinh dưỡng cho bữa ăn gia đình bạn.', 9000, N'500g', 21, 2, 41),
    (7, N'Bầu sao 1kg (2 trái)', N'Bầu sao 1kg với 2 trái tươi ngon, được thu hoạch mỗi ngày từ những luống rau sạch xanh mướt. <br> Sản phẩm không chỉ an toàn nhờ quy trình trồng hữu cơ mà còn giữ được độ tươi mới và hương vị tự nhiên nhất. <br> Thích hợp để chế biến thành các món canh thanh mát hoặc luộc chấm mắm, bầu sao là lựa chọn lý tưởng để chăm sóc sức khỏe gia đình bạn.', 18000, N'1kg', 21, 2, 20),
    (9, N'Bí xanh 1kg', N'Bí xanh 1kg – sản phẩm rau sạch organic, được nuôi dưỡng trong điều kiện tự nhiên, không phân bón hóa học hay thuốc trừ sâu. <br> Vỏ mỏng, ruột đặc, vị ngọt nhẹ, bí xanh là nguyên liệu tuyệt vời cho các món canh giải nhiệt, hấp hay nấu soup. <br> Với trọng lượng từ 2-3 trái, đây là lựa chọn thân thiện với môi trường, mang lại sự an tâm và dinh dưỡng cho mọi nhà.', 18975, N'/từ 2-3 trái', 8, 2, 32),
    (9, N'Bí xanh', N'Bí xanh 500g – món quà từ thiên nhiên, được trồng theo phương pháp hữu cơ tại các trang trại đạt tiêu chuẩn sạch. <br> Từng quả bí nhỏ gọn, vỏ xanh mướt, thịt chắc, rất phù hợp để nấu canh, làm nước ép hoặc chế biến các món ăn thanh đạm. <br> Sản phẩm không chỉ đảm bảo an toàn mà còn góp phần vào lối sống xanh, lành mạnh cho gia đình bạn.', 9488, N'500g', 19, 2, 61),
    (7, N'Mướp hương', N'Mướp hương 500g – loại rau sạch được trồng với tình yêu và trách nhiệm tại các nông trại không hóa chất. <br> Trái dài, thơm đặc trưng, thịt mềm, mướp hương là nguyên liệu lý tưởng cho các món xào, canh hay hấp đơn giản nhưng giàu dinh dưỡng. <br> Sản phẩm mang đến sự tươi ngon từ vườn đến bàn ăn, giúp bữa cơm gia đình thêm trọn vẹn và an lành.', 11925, N'500g', 30, 2, 29),
    (6, N'Khoai tây 1kg', N'Khoai tây 1kg – sản phẩm rau sạch được trồng từ những hạt giống chất lượng cao trong môi trường tự nhiên, không hóa chất. <br> Củ tròn đều, vỏ mỏng, ruột vàng, rất thích hợp để chiên, nướng, nấu súp hay làm bánh. <br> Mỗi củ khoai là kết quả của sự chăm sóc tận tâm, mang lại sự an tâm và hương vị tuyệt vời cho từng bữa ăn của bạn.', 25000, N'1kg', 21, 2, 50),
    (8, N'Khoai tây', N'Khoai tây 1kg – rau sạch được thu hoạch mỗi ngày từ các trang trại organic, đảm bảo giữ nguyên độ tươi ngon và chất lượng vượt trội. <br> Củ chắc, ngọt tự nhiên, phù hợp cho các món ăn đa dạng như khoai tây chiên, súp hay salad. <br> Sản phẩm là cam kết về sức khỏe và sự bền vững, mang đến niềm vui ẩm thực cho gia đình bạn.', 22500, N'1kg', 11, 2, 55),
    (7, N'Khoai tây 10kg', N'Khoai tây bao 10kg – lựa chọn hoàn hảo cho gia đình đông người hoặc kinh doanh, được trồng theo phương pháp sạch, không hóa chất. <br> Củ to, đều, chất lượng đồng nhất, lý tưởng để chế biến thành khoai tây chiên, nấu canh hay làm bánh. <br> Sản phẩm không chỉ tiện lợi mà còn mang lại sự an tâm tuyệt đối nhờ quy trình sản xuất bền vững và tận tâm.', 180000, N'Bao 10kg', 26, 2, 58),
    (6, N'Dưa leo', N'Dưa leo 500g – loại rau sạch được chăm sóc tỉ mỉ tại các trang trại hữu cơ, không sử dụng thuốc trừ sâu hay phân bón hóa học. <br> Quả giòn, mọng nước, vị thanh mát, rất phù hợp để ăn sống, làm salad hoặc dùng kèm các món cuốn. <br> Sản phẩm mang đến hương vị tự nhiên nhất, là lựa chọn tuyệt vời để bổ sung vitamin cho bữa ăn hàng ngày.', 11250, N'500g', 24, 2, 17),
    (7, N'Dưa leo 1kg', N'Dưa leo 1kg – rau sạch được thu hoạch mỗi ngày từ các luống rau xanh mướt, giữ nguyên độ tươi ngon và chất lượng cao cấp. <br> Quả dài, chắc, vị ngọt nhẹ, hoàn hảo cho các món salad, nước ép hoặc ăn kèm món chính. <br> Đây là sản phẩm của sự tận tâm, mang lại sự an toàn và sức khỏe cho từng thành viên trong gia đình bạn.', 22500, N'1kg', 9, 2, 61),
    (8, N'Dưa leo baby', N'Dưa leo baby 500g – loại rau sạch đặc biệt, được trồng trong điều kiện tự nhiên không hóa chất tại các trang trại uy tín. <br> Quả nhỏ xinh, giòn tan, vị ngọt thanh, rất thích hợp để ăn sống, trang trí món ăn hoặc làm đồ chua. <br> Sản phẩm không chỉ ngon mà còn là nguồn dinh dưỡng tinh khiết, góp phần làm phong phú thêm bữa ăn gia đình.', 15000, N'500g', 27, 2, 41),
    (9, N'Dưa leo giống Nhật cặp 2 trái 500g', N'Dưa leo giống Nhật 500g – sản phẩm rau sạch cao cấp, được trồng từ giống Nhật Bản nổi tiếng, không hóa chất, không thuốc trừ sâu. <br> Quả nhỏ, vỏ mỏng, ruột đặc, vị giòn ngọt đặc trưng, lý tưởng để ăn sống, làm sushi hoặc salad kiểu Nhật. <br> Tươi ngon từ vườn đến bàn ăn, đây là lựa chọn hoàn hảo cho những ai yêu thích ẩm thực lành mạnh và tinh tế.', 14250, N'500g', 24, 2, 25);
